mapper.insert:
insert into infrCodeGroup (
	ifcgSeq
	,ifcgName
	,ifcgDelNy
	,originalFileName
	,uuidFileName
) values (
	#{ifcgSeq}
	,#{ifcgName}
	,0
	,#{originalFileName}
	,#{uuidFileName}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrCodeGroup SET
	ifcgName = #{ifcgName}
WHERE 1=1
	AND ifcgSeq = #{ifcgSeq}

mapper.insert:
insert into infrCode (
	ifcdName
	, ifcdDelNy
	, infrCodeGroup_ifcgSeq
) values (
	#{ifcdName}
	, 0
	, #{infrCodeGroup_ifcgSeq}
)

mapper.update:
update infrCode set
	ifcdName = #{ifcdName}
where 1=1
	and ifcdSeq = #{ifcdSeq}
